//import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Link from 'next/link';
import post from '../axios';
import * as actions from '../actions';

const Index = ({}) => {
  const dispatch = useDispatch();

  return (
    <>
      <Link href="/detail">
        <a>Detail</a>
      </Link>
      <div>index</div>
    </>
  );
};

Index.getInitialProps = async ({ ctx }) => {
  // 请求 github 数据，因为不能暴露 token，将请求代理到服务器
  // 服务端请求默认端口是 80，需先处理
  const { store } = ctx;
  const data = await post({ url: 'http://localhost:3000/api/repository' });
  store.dispatch(actions.getGithubRepository(data));
};

export default Index;
