import { useCallback, useMemo } from 'react';
import { withRouter } from 'next/router';
import Router from 'next/router';
import { Row, Col, List } from 'antd';

import { getApiData } from '../server/utils.js';
import * as actions from '../actions';
import { getLanguageLink, getSortTypesLink } from '../utils';

const LANGUAGES = ['JavaScript', 'HTML', 'CSS', 'TypeScript', 'Java', 'Rust'];
const SORT_TYPES = [
  { name: 'Best Match' },
  { name: 'Most Stars', value: 'stars', order: 'dsc' },
  { name: 'Fewest Stars', value: 'stars', order: 'asc' },
  { name: 'Most Forks', value: 'forks', order: 'dsc' },
  { name: 'Fewest Forks', value: 'forks', order: 'asc' },
];
const selectedItemStyle = {
  borderLeft: '2px solid #6739b6',
  fontWeight: 100,
};

interface Item {
  name: string;
  value: string;
  order: string;
}

const Search = ({ router }) => {
  const { query } = router;

  const getLanguageLink = useMemo(
    () => (item: string, query: any) => getLanguageLink(item, query),
    []
  );

  const handleSortTypesLink = useMemo(
    () => (item: Item, query: any) => getSortTypesLink(item, query),
    []
  );

  return (
    <div className="root">
      <div className="menu">
        <Row gutter={20}>
          <Col span={6}>
            <List
              bordered
              header={<span className="list-header">语言</span>}
              style={{ marginBottom: 20 }}
              dataSource={LANGUAGES}
              renderItem={(item) => {
                let selected = item === query.lang;
                return (
                  <List.Item style={selected ? selectedItemStyle : null}>
                    {selected ? (
                      <span>{item}</span>
                    ) : (
                      <a href={getLanguageLink(item, query)}>{item}</a>
                    )}
                  </List.Item>
                );
              }}
            />
          </Col>
        </Row>
        <Row gutter={20}>
          <Col span={6}>
            <List
              bordered
              header={<span className="list-header">排序</span>}
              dataSource={SORT_TYPES}
              renderItem={(item: Item) => {
                let selected = false;
                if (item.name === 'Best Match' && !query.sort) {
                  selected = true;
                } else if (
                  item.value === query.sort &&
                  item.order === query.order
                ) {
                  selected = true;
                }
                return (
                  <List.Item style={selected ? selectedItemStyle : null}>
                    {selected ? (
                      <span>{item.name}</span>
                    ) : (
                      <a href={handleSortTypesLink(item, query)}>{item.name}</a>
                    )}
                  </List.Item>
                );
              }}
            />
          </Col>
        </Row>
      </div>
      <style jsx>{`
        .root {
          height: 100%;
        }
        .menu {
          height: 100%;
          overflow-x: hidden;
          overflow-y: auto;
        }
      `}</style>
    </div>
  );
};

Search.getInitialProps = async ({ ctx }) => {
  // `https://api.github.com/search/repositories?q=${name}`
  const { store } = ctx;
  const searchRepodata = await getApiData(ctx, '/api/search');
  store.dispatch(actions.getSearchRepos(searchRepodata));
};

export default withRouter(Search);
