import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Link from 'next/link';
import * as actions from '../actions';
import post from '../axios';

const Index = ({}) => {
  const dispatch = useDispatch();

  return (
    <>
      <Link href="/detail">
        <a>Detail</a>
      </Link>
      <div>index</div>
    </>
  );
};

Index.getInitialProps = async ({ ctx }) => {
  // 请求 github 数据，因为不能暴露 token，将请求代理到服务器
  // 服务器渲染默认端口为 80
  const { store } = ctx;
  /*
  const repoData = await post({ url: 'http://localhost:3000/api/repository' });
  store.dispatch(actions.getGithubRepository(repoData));
  */
  // 我的仓库
  const userRepoData = await post({
    url: 'http://localhost:3000/api/user/repos',
  });
  console.log(userRepoData);
  //store.dispatch(actions.getUserRepos(myRepoData));
};

export default Index;
