const axios = require('axios');
const { REPOSITY_URL, USER_REPOSITY_URL } = require('../config');

const getApiData = (ctx, path) => {
  let url = '';
  // 根据客户端请求的 path 匹配对应的 github api url
  switch (path) {
    case '/api/repository':
      url = REPOSITY_URL;
    case '/api/user/repos':
      url = USER_REPOSITY_URL;
    default:
      break;
  }
  // 获取 session 里的 token
  const { oauth } = ctx.req.session;
  const { token_type, access_token } = oauth ? oauth : {};
  const headers = oauth
    ? {
        Authorization: `${token_type} ${access_token}`,
      }
    : {};
  return axios({
    url,
    method: 'get',
    headers,
  }).catch((err) => {});
};

module.exports = {
  getApiData,
};
