import { memo, useMemo } from 'react';
import moment from 'moment';

export const getLastUpdated = (time: any) => {
  return moment(time).fromNow();
};

const setParamsToPath = (params: any) => {
  let { q, lang, sort, order } = params;
  q = q ? `?q=${q}` : '';
  lang = lang ? `&lang=${lang}` : '';
  sort = sort ? `&sort=${sort}` : '';
  order = order ? `&order=${order}` : '';
  let path = `/search${q}${lang}${sort}${order}`;
  return path;
};

interface I {
  value: string;
  order: string;
}

interface Q {
  q: string;
  lang: string;
  sort: string;
  order: string;
}

export const getLink = (item: string | I, query: Q) => {
  let params = {};
  if (typeof item === 'string') {
    params = { ...query, ...{ lang: item } };
  } else {
    params = { ...query, ...{ sort: item.value, order: item.order } };
  }
  return setParamsToPath(params);
};
