import { Dispatch } from 'redux';
import * as constants from '../constants';
import post from '../axios';
import { OAUTH_URL } from '../config';

interface R {
  code: number;
}

const setUserInfo = (payload: any) => ({
  type: constants.GET_USER_INFO,
  payload,
});

export const getUserInfo = (payload: any) => {
  return (dispatch: Dispatch) => {
    dispatch(setUserInfo(payload));
  };
};

const setLoginOut = () => ({
  type: constants.USER_LOGIN_OUT,
});

export const userLoginOut = () => {
  return async (dispatch: Dispatch) => {
    const data = await post({ url: '/userout' });
    data ? dispatch(setLoginOut()) : '';
  };
};

export const userPrepareOauth = async (path: string) => {
  const data = await post({ url: `/prepareOauth?url=${path}` });
  data ? (location.href = OAUTH_URL) : null;
};

const setGithubRepository = (payload) => ({
  type: constants.GET_GITHUB_REPOSITORY,
  payload,
});

export const getGithubRepository = async () => {
  const data = await post({ url: 'http://localhost:3000/api/repository' });
  return async (dispatch: Dispatch) => {
    // console.log(data);
    dispatch(setGithubRepository(data));
  };
};
