import { Tabs } from 'antd';
import { useSelector } from 'react-redux';
import atob from 'atob';
import MarkdowIt from 'markdown-it';

import { getApiData } from '../server/utils.js';
import * as actions from '../actions';

const md = new MarkdowIt();

interface S {
  detail: {
    user_repo?: {
      name: string;
      owner: any;
    };
    repo_readme?: {
      content: any;
    };
  };
}

const Detail = ({}) => {
  const userRepo = useSelector((state: S) => state.detail.user_repo);
  const repoReadme = useSelector((state: S) => state.detail.repo_readme);

  const html = md.render(atob(repoReadme.content));

  return (
    <>
      <div className="root">
        <div className="head">
          <img src={userRepo.owner.avatar_url} alt="" className="head-img" />
          <span className="head-login">{userRepo.owner.login}</span>
          <a className="head-name">{`${userRepo.name} / ${userRepo.owner.login}`}</a>
        </div>
        <Tabs defaultActiveKey="0" animated={false}>
          <Tabs.TabPane tab="文档" key="0">
            <div dangerouslySetInnerHTML={{ __html: html }}></div>
          </Tabs.TabPane>
          <Tabs.TabPane tab="讨论" key="1"></Tabs.TabPane>
        </Tabs>
      </div>
      <style jsx>{`
        .root {
          height: 100%;
          overflow-y: auto;
        }
        .head {
          display: flex;
          align-items: center;
          font-size: 12px;
        }
        .head-img {
          width: 30px;
          height: 30px;
        }
        .head-login {
          color: #666;
          margin: 0 10px;
        }
        .head-name {
          font-weight: 500;
        }
      `}</style>
    </>
  );
};

Detail.getInitialProps = async ({ ctx }) => {
  const { store } = ctx;

  const detailUserRepoData = await getApiData(ctx, '/api/detail/user');
  store.dispatch(actions.getDetailRepos(detailUserRepoData));

  const detailReposReadme = await getApiData(ctx, '/api/detail/readme');
  store.dispatch(actions.getRepoReadme(detailReposReadme));
};

export default Detail;
