import { withRouter } from 'next/router';
import { Layout, Row, Col, List } from 'antd';
import { useSelector } from 'react-redux';

import { getApiData } from '../server/utils.js';
import * as actions from '../actions';
import { getLanguageLink, getSortTypesLink } from '../utils';
import Repo from '../components/Repo';

const LANGUAGES = ['JavaScript', 'HTML', 'CSS', 'TypeScript', 'Java', 'Rust'];
const SORT_TYPES = [
  { name: 'Best Match' },
  { name: 'Most Stars', value: 'stars', order: 'dsc' },
  { name: 'Fewest Stars', value: 'stars', order: 'asc' },
  { name: 'Most Forks', value: 'forks', order: 'dsc' },
  { name: 'Fewest Forks', value: 'forks', order: 'asc' },
];
const selectedItemStyle = {
  borderLeft: '2px solid #6739b6',
  fontWeight: 100,
};
const { Sider, Content } = Layout;

interface Item {
  name: string;
  value: string;
  order: string;
}

interface S {
  home?: {
    search_repo: any;
  };
}

const Search = ({ router }) => {
  const { query } = router;

  const total_count = useSelector(
    (state: S) => state.home.search_repo.total_count
  );
  let items = useSelector((state: S) => state.home.search_repo.items);
  // useMomo 传参失败？？？
  return (
    <div className="root">
      <div className="menu">
        <Layout>
          <Sider>
            <Row gutter={20}>
              <Col span={6}>
                <List
                  bordered
                  header={<span className="list-header">语言</span>}
                  style={{ marginBottom: 20 }}
                  dataSource={LANGUAGES}
                  renderItem={(item) => {
                    let selected = item === query.lang;
                    return (
                      <List.Item style={selected ? selectedItemStyle : null}>
                        {selected ? (
                          <span>{item}</span>
                        ) : (
                          <a href={getLanguageLink(item, query)}>{item}</a>
                        )}
                      </List.Item>
                    );
                  }}
                />
              </Col>
            </Row>

            <Row gutter={20}>
              <Col span={6}>
                <List
                  bordered
                  header={<span className="list-header">排序</span>}
                  dataSource={SORT_TYPES}
                  renderItem={(item: Item) => {
                    let selected = false;
                    if (item.name === 'Best Match' && !query.sort) {
                      selected = true;
                    } else if (
                      item.value === query.sort &&
                      item.order === query.order
                    ) {
                      selected = true;
                    }
                    return (
                      <List.Item style={selected ? selectedItemStyle : null}>
                        {selected ? (
                          <span>{item.name}</span>
                        ) : (
                          <a href={getSortTypesLink(item, query)}>
                            {item.name}
                          </a>
                        )}
                      </List.Item>
                    );
                  }}
                />
              </Col>
            </Row>
          </Sider>
          <Content>
            <Row>
              <Col span={18}>
                <h3 className="repos-title">{total_count} 个仓库</h3>
                {items.map((item: any) => (
                  <Repo repo={item} key={item.id}></Repo>
                ))}
              </Col>
            </Row>
          </Content>
        </Layout>
      </div>
      <style jsx>{`
        .root {
          height: 100%;
        }
        .menu {
          height: 100%;
          overflow-x: hidden;
          overflow-y: auto;
        }
      `}</style>
    </div>
  );
};

Search.getInitialProps = async ({ ctx }) => {
  // `https://api.github.com/search/repositories?q=${name}`
  const { store } = ctx;
  const searchRepodata = await getApiData(ctx, '/api/search');
  store.dispatch(actions.getSearchRepos(searchRepodata));
};

export default withRouter(Search);
