import { useState, useCallback } from 'react';
import { Layout, Input, Avatar, Tooltip } from 'antd';
import { GithubOutlined, UserOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import { OAUTH_URL } from '../config';

const { Header, Content, Footer } = Layout;
const { Search } = Input;
const GithubOutlinedStyle = { color: 'white', fontSize: 40 };

interface S {
  user: {
    id?: number;
    avatar_url?: string;
  };
}

const LayOut = ({ children }) => {
  const user = useSelector((state: S) => state.user);
  const [searchValue, setSearchVaule] = useState('');

  const handleOnchange = useCallback((e) => {
    setSearchVaule(e.target.value);
  }, []);

  const handleOnSearch = useCallback(() => {}, []);
  return (
    <>
      <Layout>
        <Header>
          <div className="header-wrapper">
            <div className="header-left header-item">
              <GithubOutlined style={GithubOutlinedStyle} />
            </div>
            <div className="header-center header-item">
              <Search
                value={searchValue}
                onChange={handleOnchange}
                onSearch={handleOnSearch}
                placeholder="搜索仓库"
              />
            </div>
            <div className="header-right header-item">
              {user.id ? (
                <Avatar size={40} src={user.avatar_url} />
              ) : (
                <Tooltip title="请先登录">
                  <a href={OAUTH_URL}>
                    <Avatar size={40} icon={<UserOutlined />} />
                  </a>
                </Tooltip>
              )}
            </div>
          </div>
        </Header>

        <Content>{children}</Content>
        <Footer style={{ textAlign: 'center' }}>
          MyGithub ©2020 Created by White
        </Footer>
      </Layout>
      <style jsx>
        {`
          .header-wrapper {
            height: 100%;
            display: flex;
            justify-content: space-between;
          }
          .header-item {
            display: flex;
            align-items: center;
          }
          .header-center {
            width: 20%;
            margin-left: 20px;
            margin-right: auto;
          }
        `}
      </style>
      <style jsx global>
        {`
          #__next {
            height: 100%;
          }
          .ant-layout {
            height: 100%;
          }
          .ant-layout-header {
            padding: 0 20px;
            background: #6739b6;
          }
        `}
      </style>
    </>
  );
};

export default LayOut;
