import { useCallback } from 'react';
import { withRouter } from 'next/router';
import Router from 'next/router';
import { Row, Col, List } from 'antd';

const LANGUAGES = ['JavaScript', 'HTML', 'CSS', 'TypeScript', 'Java', 'Rust'];
const SORT_TYPES = [
  { name: 'Best Match' },
  { name: 'Most Stars', value: 'stars', order: 'dsc' },
  { name: 'Fewest Stars', value: 'stars', order: 'asc' },
  { name: 'Most Forks', value: 'forks', order: 'dsc' },
  { name: 'Fewest Forks', value: 'forks', order: 'asc' },
];

interface Item {
  name: string;
  value: string;
  order: string;
}

const Search = ({ router }) => {
  console.log(router);
  const { q, lang, sort, order } = router.query;

  const handleLanguageClick = useCallback((item: string) => {
    Router.push({
      pathname: '/search',
      query: {
        q,
        lang: item,
        sort,
        order,
      },
    });
  }, []);

  const handleSortTypesClick = useCallback((item: Item) => {
    Router.push({
      pathname: '/search',
      query: {
        q: name,
        lang,
        sort: item.value,
        order: item.order,
      },
    });
  }, []);

  return (
    <div className="root">
      <div className="menu">
        <Row gutter={20}>
          <Col span={6}>
            <List
              bordered
              header={<span className="list-header">语言</span>}
              style={{ marginBottom: 20 }}
              dataSource={LANGUAGES}
              renderItem={(item) => (
                <List.Item>
                  <a onClick={() => handleLanguageClick(item)}>{item}</a>
                </List.Item>
              )}
            />
          </Col>
        </Row>
        <Row gutter={20}>
          <Col span={6}>
            <List
              bordered
              header={<span className="list-header">排序</span>}
              dataSource={SORT_TYPES}
              renderItem={(item: Item) => (
                <List.Item>
                  <a onClick={() => handleSortTypesClick(item)}>{item.name}</a>
                </List.Item>
              )}
            />
          </Col>
        </Row>
      </div>
      <style jsx>{`
        .root {
          height: 100%;
        }
        .menu {
          height: 100%;
          overflow-x: hidden;
          overflow-y: auto;
        }
      `}</style>
    </div>
  );
};

Search.getInitialProps = async ({ ctx }) => {
  const { name, sort, lang, order, page } = ctx.query;
  // `https://api.github.com/search/repositories?q=${name}`
};

export default withRouter(Search);
