import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Link from 'next/link';
import * as actions from '../actions';

const Index = ({}) => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(actions.getGithubRepository());
  }, []);

  return (
    <>
      <Link href="/detail">
        <a>Detail</a>
      </Link>
      <div>index</div>
    </>
  );
};

Index.getInitialProps = async ({ ctx }) => {
  // 请求 github 数据，因为不能暴露 token，将请求代理到服务器
  //actions
  /*
  const { store } = ctx;
  store.dispatch(actions.getGithubRepository());
  */
};

export default Index;
