import { useSelector } from 'react-redux';
//import Link from 'next/link';
import { Button } from 'antd';

import * as actions from '../actions';
//import post from '../axios';
//import { getApiData } from '../server/utils.js';

interface S {
  user: { id?: number };
}

const Index = ({}) => {
  const { id } = useSelector((state: S) => state.user);
  return (
    <>
      {!id ? (
        <div className="noLoginBtn">
          <Button type="primary">点击登录</Button>
        </div>
      ) : (
        <div>index</div>
      )}
      <style jsx>
        {`
          .noLoginBtn {
            margin: auto;
          }
        `}
      </style>
    </>
  );
};

Index.getInitialProps = async ({ ctx }) => {
  // 请求 github 数据，因为不能暴露 token，将请求代理到服务器
  // 服务器渲染默认端口为 80
  const { store } = ctx;
  /*
  const repoData = await getApiData(ctx, '/api/repository');
  store.dispatch(actions.getGithubRepository(repoData));
  */
  /*  查看用户仓库信息需先登录
  const user_id = store.getState().user.id;
  if(!user_id) {
    return;
  }
  */
  //const userRepoData = await getApiData(ctx, '/api/user/repos');
  //store.dispatch(actions.getUserRepos(userRepoData));
  //const userStarredRepoData = await getApiData(ctx, '/api/user/starred');
  //store.dispatch(actions.getUserRepos(userStarredRepoData));
};

export default Index;
