const getParamsToPath = (params) => {
  let keys = Object.keys(params);
  let values = Object.values(params);
  let path = '';
  keys.forEach(function (item, index) {
    let str = `${item}=${values[index]}`;
    if (!index) {
      path += `?${str}`;
      return;
    }
    path += `&${str}`;
  });
  return path;
};

module.exports = {
  getParamsToPath,
};
