import moment from 'moment';
import { string } from 'prop-types';

export const getLastUpdated = (time: any) => {
  return moment(time).fromNow();
};

interface Q {
  q: string;
  lang: string;
  sort: string;
  order: string;
}

export const getLanguageLink = (item: string, query: Q) => {
  let params = { ...query, ...{ lang: item } };
  let { q, lang, sort, order } = params;
  q = q ? `?q=${q}` : '';
  lang = lang ? `&lang=${lang}` : '';
  sort = sort ? `&sort=${sort}` : '';
  order = order ? `&order=${order}` : '';
  let path = `/search${q}${lang}${sort}${order}`;
  return path;
};
