const getParamsToString = (params) => {
  let keys = Object.keys(params);
  let values = Object.values(params);
  let path = '';
  keys.forEach(function (item, index) {
    let str = `${item}=${values[index]}`;
    if (!index) {
      path += `?${str}`;
      return;
    }
    path += `&${str}`;
  });
  return path;
};

const getQueryToSlash = (query) => {
  let values = Object.values(query);
  let path = '';
  values.forEach(function (item) {
    path += `/${item}`;
  });
  return path;
};

module.exports = {
  getParamsToString,
  getQueryToSlash,
};
