const axios = require('axios');

const {
  SEARCH_REPOSITY_URL,
  USER_REPOSITY_URL,
  USER_STAR_URL,
  REPOSITY_DETAIL_URL,
} = require('../config');

const getApiData = async (ctx, path) => {
  // 根据客户端请求的 path 匹配对应的 github api url
  let url = '';
  let params = '';
  params = { ...ctx.query };
  console.log(params);
  switch (path) {
    case '/api/user/repos':
      url = USER_REPOSITY_URL;
      break;
    case '/api/user/starred':
      url = USER_STAR_URL;
      break;
    case '/api/search':
      url = SEARCH_REPOSITY_URL;
      break;
    case '/api/detail':
      url = REPOSITY_DETAIL_URL;
      break;
    default:
      break;
  }

  // 判断当前为请求环境为服务端还是客户端（网页刷新和链接跳转）
  let isServer = typeof window === 'undefined';
  let headers = {};

  // 客户端请求没有 ctx.session (ctx.req.session)，需代理到服务器上
  if (!isServer) {
    console.log(ctx);
    // ctx.asPath = search?q=&lang=JavaScript&sort=&order=
    let path = '/api' + ctx.asPath;
    const result = await axios({ url: path, method: 'get' });
    console.log(result);
    let data = result.data.code === 0 ? result.data.data : [];
    return data;
  } else {
    // 服务端可直接获取 ctx
    const { oauth } = ctx.req.session;
    const { token_type, access_token } = oauth ? oauth : {};
    headers = oauth
      ? {
          Authorization: `${token_type} ${access_token}`,
        }
      : {};
    const result = await axios({
      url,
      method: 'get',
      data: params,
      headers,
    }).catch((err) => {});
    console.log('服务端获取数据成功');
    let data = result ? result.data : [];
    return data;
  }
};

module.exports = {
  getApiData,
};
